mapper.select:
SELECT p.id, p.title, p.author, p.content, p.views, p.type_id AS typeId, p.create_time AS createTime, t.name AS typeName FROM post p LEFT JOIN type t ON p.type_id = t.id
AND p.title LIKE CONCAT('%', #{title}, '%')
AND p.author LIKE CONCAT('%', #{author}, '%')
AND p.views >= #{minViews}
AND p.type_id = #{typeId}
ORDER BY p.create_time DESC
